client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/components/ApplicationCard.ts.content:
import { Star } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import * as LucideIcons from 'lucide-react';

interface ApplicationCardProps {
  id: string;
  name: string;
  description: string;
  icon: string;
  color: string;
  apiCount: number;
  isFavorite: boolean;
  onToggleFavorite: () => void;
  onSelect: () => void;
}

const colorClasses: Record<string, string> = {
  blue: 'bg-app-blue/10 text-app-blue',
  green: 'bg-app-green/10 text-app-green',
  purple: 'bg-app-purple/10 text-app-purple',
  orange: 'bg-app-orange/10 text-app-orange',
  yellow: 'bg-app-yellow/10 text-app-yellow',
  pink: 'bg-app-pink/10 text-app-pink',
  teal: 'bg-app-teal/10 text-app-teal',
  indigo: 'bg-app-indigo/10 text-app-indigo',
};

export function ApplicationCard({
  id,
  name,
  description,
  icon,
  color,
  apiCount,
  isFavorite,
  onToggleFavorite,
  onSelect,
}: ApplicationCardProps) {
  const Icon = (LucideIcons as any)[icon] || LucideIcons.Box;

  return (
    <Card className="group relative p-6 transition-all hover:shadow-md" data-testid={`card-app-${id}`}>
      <button
        onClick={onToggleFavorite}
        className="absolute right-4 top-4 hover-elevate active-elevate-2 rounded-full p-1"
        data-testid={`button-favorite-${id}`}
      >
        <Star
          className={`h-5 w-5 transition-colors ${
            isFavorite ? 'fill-app-yellow text-app-yellow' : 'text-muted-foreground'
          }`}
        />
      </button>

      <div className="space-y-4">
        <div className={`inline-flex h-12 w-12 items-center justify-center rounded-lg ${colorClasses[color]}`}>
          <Icon className="h-6 w-6" />
        </div>

        <div className="space-y-2">
          <div className="flex items-center gap-2">
            <h3 className="text-xl font-semibold" data-testid={`text-app-name-${id}`}>{name}</h3>
            <Badge variant="secondary" className="text-xs" data-testid={`badge-api-count-${id}`}>
              {apiCount} APIs
            </Badge>
          </div>
          <p className="line-clamp-2 text-sm text-muted-foreground">{description}</p>
        </div>

        <Button
          onClick={onSelect}
          className="w-full"
          data-testid={`button-select-${id}`}
        >
          Select
        </Button>
      </div>
    </Card>
  );
}

